pragma solidity ^0.8.0;

contract MessageMessenger {
    // Mapping of messages stored on the blockchain
    mapping (uint256 => string) public messages;

    // Mapping of users who have made changes to messages
    mapping (uint256 => mapping (uint256 => bool)) public changeLogs;

    // Event emitted when a message is updated
    event MessageUpdated(uint256 _messageId, bool _update);

    // Event emitted when a user makes a change to a message
    event UserMadeChange(uint256 _messageId, uint256 _oldId, uint256 _newId);

    // Constructor
    constructor() public {
        // Initialize messages
        for (uint256 i = 0; i < 10; i++) {
            messages[i] = "";
        }
        // Initialize change logs
        for (uint256 i = 0; i < 5; i++) {
            changeLogs[i] = false;
        }
    }

    // Update a message
    function updateMessage(uint256 _messageId, string memory _newMessage) public {
        // Check if the message is already in the contract's storage
        require(_messageId < 10, "Message not found in the contract's storage");
        // Check if the message was updated by this contract or a trusted operator
        require(address(this).call.value(_newMessage.length()).value == 0 &&!changeLogs[_messageId][changeLogs[_messageId].length - 1], "This contract did not make the update.");
        // Update the message in the contract's storage
        messages[_messageId] = _newMessage;
        // Add the update to the change logs
        changeLogs[_messageId][changeLogs[_messageId].length - 1] = true;
        // Emit an event
        emit MessageUpdated(_messageId, true);
    }

    // Check if a message is readable (public) or private
    function isMessageReadable(uint256 _messageId) public view returns (bool) {
        return messages[_messageId].length > 0;
    }

    // Check if a message is writable (private) or public
