import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class BankService {
  private bank: Bank;

  constructor(bank: Bank) {
    this.bank = bank;
  }

  getBalance(): number {
    return this.bank.getBalance();
  }
}
```

