**Contract Code:**
```solidity
pragma solidity ^0.8.0;

contract BlockchainMessenger {
    mapping(address => string) public messages;
    uint public messageCount = 0;
    mapping(uint => address) public messageOwners;

    function sendMessage(string memory _message) public {
        require(messageCount < 10, "Message count exceeded");
        messages[msg.sender] = _message;
        messageCount++;
        messageOwners[messageCount] = msg.sender;
    }

    function getMessage(string memory _message) public view returns (string memory, uint) {
        require(messageCount > 0, "No messages");
        return (messages[msg.sender], messageCount);
    }

    function getOwnerOf(uint _messageIndex) public view returns (address) {
        require(messageCount > _messageIndex, "No messages");
        require(messageCount >= _messageIndex, "Index out of range");
        return messageOwners[_messageIndex];
    }

    function updateMessage(uint _messageIndex, string memory _newMessage) public {
        require(messageCount > _messageIndex, "No messages");
        require(messageCount >= _messageIndex, "Index out of range");
        require(messageOwners[_messageIndex]!= msg.sender, "Cannot update private message");
        messages[_messageIndex] = _newMessage;
        messageCount++;
        messageOwners[_messageIndex] = msg.sender;
    }
}
```
