**Contract Code**
```solidity
pragma solidity ^0.8.0;

import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol";

contract BlockchainMessenger is Ownable {
    mapping (string => bool) public messageCount;
    mapping (string => string) public messageText;
    mapping (address => mapping (string => bool)) public messageWriter;

    address public owner;
    string public message;

    constructor() {
        owner = msg.sender;
    }

    // Function to add a message
    function addMessage(string memory _text) public {
        require(messageCount[_text] == 0, "Message already exists");
        require(blocklog.size() == 0, "Message has already been added");
        messageCount[_text] = 1;
        messageText[_text] = _text;
        messageWriter[msg.sender][_text] = true;
        // Create a transaction log
        blocklog.push(_text);
    }

    // Function to update a message
    function updateMessage(string memory _text) public {
        require(blocklog.size() > 0, "No transaction log exists");
        require(messageCount[_text] > 0, "Message does not exist");
        require(messageWriter[msg.sender][_text] == false, "Message is being updated");
        messageText[_text] = _text;
        messageWriter[msg.sender][_text] = true;
        messageCount[_text] = 1;
        // Create a new transaction log
        blocklog.push(_text);
    }

    // Function to delete a message
    function deleteMessage(string memory _text) public {
        require(blocklog.size() > 0, "No transaction log exists");
        require(messageCount[_text] > 0, "Message does not exist");
        require(messageWriter[msg.sender][_text] == false, "Message is being deleted");
        delete messageText[_text];
        delete messageWriter[msg.sender][_text];
        messageCount[_text] = 0;
        blocklog.push("");
    }
}
```
