contract BlockchainMessenger {
    // Mapping of message sender to their messages
    mapping(address => mapping(string => uint256)) public messages;

    // Mapping of message IDs to their corresponding message data
    mapping(uint256 => string) public messageId;

    // Event emitted when a message is updated
    event UpdatedMessage(string indexed message, uint256 indexed id);

    // Event emitted when a new message is added
    event NewMessage(string indexed message, address indexed sender, uint256 indexed id);

    // Constructor function
