import "https://github.com/OpenZeppelin/openzeppelin-solidity/contracts/token/ERC721/SafeERC721.sol";
import "https://github.com/OpenZeppelin/openzeppelin-solidity/contracts/immutable/Unicorns.sol";

contract BlockchainMessenger {
    // Unicorns - Token yang dapat dipakai untuk save, share, dan lain-lain
    Unicorns public immutable unicorn;

    // Baris - Baris token untuk save teks, dapat dibuka oleh pengguna yang pernah menggunakan baris
    address private immutable baris;
    Unicorns public immutable barisToken;

    // Konfigurasi kecil
    string public _name = "Blockchain Messenger";
    uint public _address = 0x0000000000;
    uint public _network = 1;

    // Konstruktor
    constructor() public {
        // Inisialisasi Baris
        baris = Unicorns.new(this, _name, _address, _network);
        // Inisialisasi token Baris
        barisToken = Unicorns(new Unicorns(this), _name, _address, _network);
    }

    // Function untuk mengimpan teks pada blockchain
    function saveText(string memory _text) public {
        // Memastikan bahwa pengguna adalah pengguna yang telah memberikan ijin untuk menggunakan baris
        require(msg.sender == barisToken.owner, "Permintaan save teks tidak sesuai dengan ijin");
        // Inisialisasi jumlah perbaruan teks
        uint256 _previousCount = msg.value;
        // Simpan teks ke dalam blockchain
        unicorn.saveText(_text);
        // Hitung jumlah perubahan
        uint256 _newCount = (uint256(unicorn.count) + 1);
        // Perbarui jumlah perbaruan
        _newCount = _newCount - _previousCount;
        // Membagikan perubahan jumlah perbaruan dan teks ke koin
        emit UpdateCount(_newCount, _text);
        emit UpdateText(_text);
    }

    // Function untuk mengacu pada teks pada blockchain
    function getText(string memory _text) public view returns (string memory) {
        // Memastikan bahwa pengguna adalah pengguna yang telah memberikan ijin untuk menggunakan baris
        require(msg.sender == barisToken.owner, "Permintaan getText tidak sesuai dengan ijin");
        // Cast teks ke dalam string
        return unicorn.getText(_text);
    }
}

