pragma solidity ^0.8.0;

contract MessageMessenger {
    // Mapping of messages to their timestamps
    mapping (address => uint) public messageTimestamps;

    // Mapping of messages to their recipients
    mapping (address => uint) public messageRecipients;

    // Mapping of messages to their updates
    mapping (address => mapping (uint => bool)) public messageUpdates;

    // Event emitted when a message is received
    event MessageReceived(address recipient, uint timestamp, bool updated);

    // Function to send a message to a recipient
    function sendMessage(address recipient, string memory message) public {
        require(msg.sender!= address(0), "Recipient address is empty");
        require(!messageRecipients[msg.sender], "Message has already been sent");
        messageRecipients[msg.sender] = true;
        uint timestamp = block.timestamp;
        messageTimestamps[msg.sender] = timestamp;
        messageUpdates[msg.sender][timestamp] = true;
        emit MessageReceived(recipient, timestamp, true);
    }

    // Function to update a message
    function updateMessage(uint index, bool updated) public {
        require(index > 0, "Message index must be greater than 0");
        require(!messageUpdates[msg.sender][index], "Message has already been updated");
        messageUpdates[msg.sender][index] = updated;
        uint timestamp = block.timestamp;
        messageTimestamps[msg.sender] = timestamp;
        emit MessageReceived(messageRecipients[msg.sender], timestamp, updated);
    }

    // Function to delete a message
    function deleteMessage(uint index) public {
        require(index > 0, "Message index must be greater than 0");
        require(!messageUpdates[msg.sender][index], "Message has not been updated");
        delete messageUpdates[msg.sender][index];
        uint timestamp = block.timestamp;
        messageTimestamps[msg.sender] = timestamp;
        emit MessageReceived(messageRecipients[msg.sender], timestamp, false);
    }
}
```

