pragma solidity ^4.6.0;
import "https://github.com/OpenZeppelin/openzeppelin-solidity/tree/master/contracts/utils/AccessControl.sol";
import "https://github.com/OpenZeppelin/openzeppelin-solidity/tree/master/contracts/utils/Counters.sol";

contract BlockchainMessenger {
    // Mapping of messages to users
    mapping (uint256 => User) public messages;

    // Mapping of messages to their counts
    mapping (uint256 => uint256) public messageCounts;

    // User contract for private messages
    contract User {
        // Mapping of users to their private messages
        mapping (address => mapping(uint256 => bool)) public privateMessages;

        function privateMessage(address sender, uint256 messageIndex, bytes memory message) public {
            // Set the private message flag to true
            privateMessages[sender][messageIndex] = true;
        }

        // Private function to get the private message count
        function privateMessageCount(address sender) public view returns (uint256) {
            return privateMessages[sender].length;
        }
