            // Update the messageCount storage
            messageCount.incrementedMessageCount = incrementedMessageCount;
        }
    }

    // Private function to store a message
    function storeMessage(uint256 _messageId, bytes memory _message) private {
        // Get the users who have received the message
        for (address user of messages[messageCounts[_messageId]]) {
            // Set the private message flag to false
            privateMessages[user][messageCounts[_messageId] + _messageId] = false;
