contract Counter {
  address public owner;
  mapping (address => uint256) public messages;

  constructor() public {
    owner = msg.sender;
  }

  function addMessage(address _address, string _message) public {
    messages[_address] = _message;
  }

  function getMessage(address _address) public view returns (string) {
    return messages[_address];
  }
}
```

