import { Component1Service } from './component1.service';

@Injectable({
  providedIn: 'root'
})
export class Component2Service {
  private component1Service: Component1Service = new Component1Service();

  getObservable(): Observable<any> {
    return this.component1Service.getObservable();
  }
}
