import { BrowserModule } from '@angular/platform-browser';
import { AppComponent } from './app.component';
import { AccountsService } from './accounts.service'; // dependency injection module and service

@NgModule({
  declarations: [AppComponent],
  imports: [BrowserModule, AccountsService],
  providers: [AccountsService], // dependency injection module and service
