    event NewMessage(address indexed sender, string message);

    // Event emitted when a message is updated
    event UpdateMessage(address indexed sender, string updatedMessage);

    // Event emitted when a message is read
    event ReadMessage(address indexed sender, string message);

    // Store the latest message index
    uint256 public latestMessageIndex;

    // Initialize the latest message index to 0
    uint256 public latestMessageIndex = 0;

    // Function to send a message
    function sendMessage(string memory message) public {
        // Check if the message count exists for the sender
        require(messageCounts[msg.sender] == 0, "Message count already exists.");

        // Increment the message count for the sender
        messageCounts[msg.sender] = latestMessageIndex + 1;

        // Update the latest message index
        latestMessageIndex = latestMessageIndex + 1;

        // Emit the NewMessage event
        emit NewMessage(msg.sender, message);
    }

    // Function to update a message
    function updateMessage(string memory message) public {
        // Check if the message count exists for the sender
        require(messageCounts[msg.sender] > 0, "Message count does not exist.");

        // Decrement the message count for the sender
