pragma solidity ^0.8.0;

contract BlockchainMessenger {
    // Mapping of messages to their owners
    mapping (address => mapping (string => string)) public messages;

    // Mapping of message counts to their owners
    mapping (address => mapping (string => uint)) public countToOwners;

    // Event emitted when a message is saved or updated
    event MessageSaved(address indexed owner, string message);

    // Event emitted when a message is updated
    event MessageUpdated(address indexed owner, string message, uint newCount);

    // Address of the owner of the contract
    address public owner;

    // Function to save a message on the blockchain
    function saveMessage(string memory message) public {
        // Check if the message has already been saved to the contract
