import java.util.HashMap;
import java.util.Map;
import java.util.Scanner;

public class Main {
    public static int minReplacement(String s) {
        Map<Character, Integer> freq = new HashMap<>();
        for (char c : s.toCharArray()) {
            freq.put(c, freq.getOrDefault(c, 0) + 1);
        }

        int n = s.length();
        int balance = (n + 3) / 4;
        int min = balance * 4;
        int max = (balance * 4) + 1;
        if (min <= n && n <= max) {
            int cur = 0;
            int replace = 0;
            for (int i = 0; i < n; i++) {
                if (freq.get(s.charAt(i))!= balance) {
                    int diff = Math.abs((int) (freq.get(s.charAt(i)) - balance));
                    cur += diff * 4;
                    replace = i + 1;
                    break;
                }
            }
            if (cur <= max) {
                return replace;
            } else {
                return 0;
            }
        } else {
            return 0;
        }
    }

