class Solution {
    def connect_nodes(self, root: 'Node') -> None:
        if not root:
            return
        if root.right:
            root.right.next = root
        else:
            root.next = root.right
        self.connect_nodes(root.left)
        self.connect_nodes(root.right)

    def print_order(self, root: 'Node') -> list[int]:
        if not root:
            return []
        else:
            self.print_order(root.left)
            self.print_order(root.right)
            return [root.val] + self.print_order(root.right)

    def print_tree_with_next(self, root: 'Node') -> list[int]:
        if not root:
            return []
        else:
            self.print_tree_with_next(root.left)
            return self.print_tree_with_next(root.right) + [root.val]
}

class Node:
    def __init__(self, val, left=None, right=None, next=None):
        self.val = val
        self.left = left
        self.right = right
        self.next = next

class Solution2:
    def connect_nodes(self, root: 'Node') -> 'Node':
        if not root:
            return root

        else:
            if not root.right:
                root.right = root.next
            else:
                root.right = self.connect_nodes(root.right)
            return root

    def print_order(self, root: 'Node') -> list[int]:
        if not root:
            return []
        else:
            if root.right and root.next:
                root.right = root.next
            else:
                root.next = root.right
            return self.print_order(root.left) + self.print_order(root.right)

